/**
 * Common utilities used in Kosko.
 *
 * @packageDocumentation
 */

/**
 * Returns `code` of an error-like object.
 *
 * @public
 */
export declare function getErrorCode(err: unknown): string | undefined;

/**
 * Returns true when type of `value` is `object` and is not `null`, `undefined` or
 * an array.
 *
 * @public
 */
export declare function isRecord(value: unknown): value is Record<string | symbol, unknown>;

/**
 * Transforms `input` into an array, or leave it as-is if `input` is already an array.
 *
 * @public
 */
export declare function toArray<T>(input: T | T[]): T[];

export { }
