/**
 * Imports and resolves modules.
 *
 * @packageDocumentation
 */

/**
 * Returns file extensions which can be imported.
 *
 * @defaultValue Node.js
 * ```js
 * [".cjs", ".mjs", ".js", ".json", ".node"]
 * ```
 *
 * @public
 */
export declare function getRequireExtensions(): string[];

/**
 * Imports a module from the given `path`.
 *
 * @param path - Absolute path to a module. It must not be a file URL or a relative path.
 * @public
 */
export declare function importPath(path: string): Promise<any>;

/**
 * Resolves path to the specified module.
 *
 * @public
 */
export declare function resolve(id: string, options?: ResolveOptions): Promise<string | undefined>;

/**
 * @public
 */
export declare interface ResolveOptions {
    /**
     * The directory to resolve from.
     *
     * @defaultValue `process.cwd()`
     */
    baseDir?: string;
    /**
     * File extensions to resolve.
     *
     * @defaultValue `getRequireExtensions()`
     */
    extensions?: readonly string[];
}

